                    });

                    req.session.token = token;
                    req.session.save();
                    return res.status(200).json({
                        title: 'Auth successful',
                        message: "Auth successful",
                        token: token
                    });
                }

                return res.status(400).json({
                    title: 'Auth failed',
                    message: 'Invalid credentials'
                });

            });
        })
        .catch(error => {
            return res.status(400).json({
                error : error,
                title: 'Server error',
                message: 'Something was wrong...'
            });
        });
}

